ogr2ogr -dim XY -f CSV -lco GEOMETRY=AS_WKT -lco GEOMETRY_NAME=boundary -nlt MULTIPOLYGON -sql "SELECT Name AS name,description AS description FROM \"Neighborhood Association Boundaries\" WHERE OGR_GEOMETRY='MultiPolygon' or OGR_GEOMETRY='Polygon'" -skipfailures ./files/neighborhood_association_boundaries.csv "/vsicurl/https://www.google.com/maps/d/kml?mid=1wWZZ8zxo50QUP9_VKePCquh9D_FWk-w&forcekml=1"

# ogr2ogr -dialect SQLITE -dim XY -f CSV -lco GEOMETRY=AS_WKT -lco GEOMETRY_NAME=geometry -nlt MULTILINESTRING -sql "SELECT geometry,Name as name,LOWER(Direction__both_one_) as direction,LOWER(Type__hazard_construction_event_) as type,Estimate_closure_date__mm_dd_yyyy_ as estimated_closure_date,Estimated_open_date__mm_dd_yyyy_ as estimated_open_date,Closure_time__if_necessary_ as estimated_closure_time,Closure_days_of_week__if_necessary_ as closure_days_of_week FROM \"Road Closures\"" -skipfailures ./files/road_closures.csv "/vsicurl/https://www.google.com/maps/d/kml?mid=1CNXFi5H3Ix1IELX9S02_63K5WVg&forcekml=1"
